import React, {useState} from 'react';

const Weather = () => {
    const [form, setForm] = useState(
        {
            city:'',
        }
    )

    const [weather, setWeather] = useState( '')

    function sendCity(e) {
        e.preventDefault();
        let value = form.city;

        if (form.city !== '')
        {
            if (form.city.includes(' '))
            {
                value = value.replaceAll(" ", "-");
                setForm({...form, city: value});
            }

            
            //console.log(value);
        } else {
            alert('add values');
        }
    }

    function handleChange(e) {
        // Target - это dom-элемент, а value - значение внутри него
        let name = e.target.name;
        let value = e.target.value;

        // если изменяется инпут, то
        if (name === 'city') {
            setForm({...form, city: value})
        }
    }

    return (
        <div>
            <form className="weather-search">
                <input className="weather-input"
                       type='text'
                       name='city'
                       // Target - это dom-элемент, а value - значение внутри него
                       onChange = {e => handleChange(e)}
                       placeholder='Set your location here'/>
                <button className='weather-button' onClick={e => sendCity(e)} >
                    Нажми на меня
                </button>
            </form>
        </div>
    );
};

export default Weather;
