import React, {useState} from 'react';

const Weather = () => {
    const [form, setForm] = useState(
        {
            city:'',
        }
    )

    function kek(e) {
        e.preventDefault();
        if (form.city !== '')
        {
            if (e.target.value.contains("0"))
                console.log('fff');
            //if (form.city.value.contains(" "))

            console.log(form.city);
        } else {
            alert('add values');
        }
    }

    function inputChanges(e) {
        let name = e.target.name;
        let value = e.target.value;

        // если изменяется инпут, то
        if (name === 'city') {
            setForm({...form, city: value})
        }
    }

    return (
        <div>
            <form className="weather-search">
                <input className="weather-input"
                       type='text'
                       name='city'
                       // Target - это dom-элемент, а value - значение внутри него
                       onChange = {e => inputChanges(e)}
                       placeholder='Set your location here'/>
                <button className='weather-button' onClick={e => kek(e)} >
                    Нажми на меня
                </button>
            </form>
        </div>
    );
};

export default Weather;
