import React, {useState} from 'react';
import ShowWeather from "./ShowWeather";

const Weather = () => {
    // const [form, setForm] = useState(
    //     {
    //         city:'',
    //     }
    // )

    const [city, setCity] = useState(
        {
            city:'',
        }
    )

    const [title, setTitle] = useState('');

    function sendCity(e) {
        e.preventDefault();
        let value = title;

        if (value !== '')
        {
            if (value.includes(' '))
            {
                value = value.replaceAll(" ", "-");
                //setForm({...form, city: value});
            }

            setTitle('');
            setCity(value);
            console.log(value);
        } else {
            alert('add values');
        }
    }

    // function handleChange(e) {
    //     // Target - это dom-элемент, а value - значение внутри него
    //     let name = e.target.name;
    //     let value = e.target.value;
    //
    //     // если изменяется инпут, то
    //     if (name === 'city') {
    //         setForm({...form, city: value})
    //     }
    // }

    return (
        <div>
            <form className="weather-search">
                <input className="weather-input"
                       value={title}
                       type='text'
                       name='city'
                       // Target - это dom-элемент, а value - значение внутри него
                       onChange = {e => setTitle(e.target.value)}
                       placeholder='Set your location here'/>
                <button className='weather-button' onClick={e => sendCity(e)} >
                    Нажми на меня
                </button>
            </form>

            {
                (city !== '') ?
                    <div>
                        твой город - {city}
                        {/*<ShowWeather data={city.city}/>*/}
                    </div> :
                    null
            }
        </div>
    );
};

export default Weather;
